sp_widget.client_script:
function($scope, spUtil) {
  /* widget controller */
  var c = this;
	$scope.orderField = "number";

	$scope.changeSort = function(field){
		$scope.orderField = field;
	}
	//Pass data from model to view (click handler, event handler)

	//watch incident table if there's a change to the data
	//if there is a change call server script to get updated records
	spUtil.recordWatch($scope, "incident","",function(name,data){
		spUtil.update($scope);
	});

}

sp_widget.css:
table, th, td{
  border:1px solid black;
}

sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */

	//MODEL - fill with data
	data.incidents = [];//arbitary reference to a variable

	var gr = new GlideRecord('incident');
	gr.addActiveQuery();
	gr.setLimit(10);
	gr.orderByDesc('sys_updated_on');
	gr.query();

	//pull out just the values you want rather than the whole glide record - to speed efficiency
	while(gr.next()){
		var incident = {}
		incident.number = gr.getDisplayValue('number');
		incident.short_description = gr.getDisplayValue('short_description');
		incident.sys_id = gr.getUniqueValue();
		incident.sys_updated_on = gr.getValue('sys_updated_on');
		data.incidents.push(incident);
	}

})();
