sysauto_script.script:
var autoCloseDays = gs.getProperty('x_58872_needit.autoCloseOverdue');

var rightNow = new GlideDateTime();

// Query the database for NeedIt Task records with Due date field values older
// then auto close overdue property.  Only return NeedIt Task records that do not have
// a State field value of Closed Complete.
var overdueNITasks = new GlideRecord('x_58872_needit_needit_task');
overdueNITasks.addQuery('due_date','<=',gs.daysAgo(autoCloseDays));
overdueNITasks.addQuery('state','<',3);

overdueNITasks.query();
// Write a log message for each overdue NeedIt Task record
// Generate an event for each overdue NeedIt Task record
while(overdueNITasks.next()){
	gs.info("Auto close NeedIt Task record = " + overdueNITasks.number);
	overdueNITasks.work_notes = "Record closed because it was too far past the Due date.";
	overdueNITasks.state = 4;
	overdueNITasks.update();

}
